import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";

const History = ({ onFileSelect }) => {
  const [files, setFiles] = useState([]);
  const { API } = useAuth();

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = async () => {
    try {
      const res = await API.get("/upload/history");
      setFiles(res.data);
    } catch (error) {
      console.error("Error fetching history:", error);
    }
  };

  return (
    <div className="card">
      <h3>Upload History</h3>
      {files.length === 0 ? (
        <p>No files uploaded yet</p>
      ) : (
        <div>
          {files.map(file => (
            <div key={file._id} style={{border: "1px solid #ddd", padding: "10px", margin: "5px 0"}}>
              <p><strong>{file.originalName}</strong></p>
              <p>Uploaded: {new Date(file.createdAt).toLocaleDateString()}</p>
              <button onClick={() => onFileSelect(file)} className="btn btn-primary">
                Load for Analysis
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default History;
